#import "MyNSObjectController.h"@implementation MyNSObjectController- (IBAction)formatCheck:(id)sender{	/* NSLog(@"formatCheck"); */	if ( [self formatEnabledValue] ) {		[(NSTextField *)formatWid setEnabled:YES];		[(NSTextField *)formatHt setEnabled:YES];		[(NSTextField *)formatPixelAspect setEnabled:YES];	} else {		[(NSTextField *)formatWid setEnabled:NO];		[(NSTextField *)formatHt setEnabled:NO];		[(NSTextField *)formatPixelAspect setEnabled:NO];		[openGLView display];	}}- (IBAction)formatWidthChanged:(id)sender{	[openGLView display];}- (IBAction)formatHeightChanged:(id)sender{	[openGLView display];}- (IBAction)formatPixelAspectChanged:(id)sender{	[openGLView display];}- (void) format:(float)wid height:(float)ht pixelAspect:(float)pa;{	/* NSLog(@"MyNSObjectController:format"); */    if ( [(NSButton *)formatEnable intValue] == 0 ) {		[(NSTextField *)formatWid setFloatValue:wid];		[(NSTextField *)formatHt setFloatValue:ht];		[(NSTextField *)formatPixelAspect setFloatValue:pa];	}	}- (int) formatEnabledValue{	return [(NSButton *)formatEnable intValue];}- (float) formatWidthValue{	return [(NSTextField *)formatWid floatValue];}- (float) formatHeightValue{	return [(NSTextField *)formatHt floatValue];}- (float) formatPixelAspectValue{	return [(NSTextField *)formatPixelAspect floatValue];}@end