#import "MyNSObjectController.h"@implementation MyNSObjectController- (IBAction)displayCheck:(id)sender{	/* NSLog(@"displayCheck"); */	if ( [self displayEnabled] ) {		[(NSTextField *)displayOriginX setEnabled:YES];		[(NSTextField *)displayOriginY setEnabled:YES];	} else {		[(NSTextField *)displayOriginX setEnabled:NO];		[(NSTextField *)displayOriginY setEnabled:NO];		[openGLView display];	}}- (IBAction)displayOriginXChanged:(id)sender{	[openGLView display];}- (IBAction)displayOriginYChanged:(id)sender{	[openGLView display];}- (IBAction)formatCheck:(id)sender{	/* NSLog(@"formatCheck"); */	if ( [self formatEnabled] ) {		[(NSTextField *)formatWid setEnabled:YES];		[(NSTextField *)formatHt setEnabled:YES];		[(NSTextField *)formatPixelAspect setEnabled:YES];	} else {		[(NSTextField *)formatWid setEnabled:NO];		[(NSTextField *)formatHt setEnabled:NO];		[(NSTextField *)formatPixelAspect setEnabled:NO];		[openGLView display];	}}- (IBAction)formatWidthChanged:(id)sender{	[openGLView display];}- (IBAction)formatHeightChanged:(id)sender{	[openGLView display];}- (IBAction)formatPixelAspectChanged:(id)sender{	[openGLView display];}- (IBAction)frameAspectCheck:(id)sender{	/* NSLog(@"frameAspectCheck"); */	if ( [self frameAspectEnabled] ) {		[(NSTextField *)frameAspect setEnabled:YES];	} else {		[(NSTextField *)frameAspect setEnabled:NO];		[openGLView display];	}}- (IBAction)frameAspectChanged:(id)sender;{	[openGLView display];}/* ------------------------------------------------------------------------- */- (void) formatWidth:(float)wid height:(float)ht pixelAspect:(float)pa;{	/* NSLog(@"MyNSObjectController:format"); */    if ( [(NSButton *)formatEnable intValue] == 0 ) {		[(NSTextField *)formatWid setFloatValue:wid];		[(NSTextField *)formatHt setFloatValue:ht];		[(NSTextField *)formatPixelAspect setFloatValue:pa];	}	}- (int) formatEnabled{	return [(NSButton *)formatEnable intValue];}- (float) formatWidthValue{	return [(NSTextField *)formatWid floatValue];}- (float) formatHeightValue{	return [(NSTextField *)formatHt floatValue];}- (float) formatPixelAspectValue{	return [(NSTextField *)formatPixelAspect floatValue];}- (int) displayEnabled{	return [(NSButton *)displayEnable intValue];}- (void) displayOriginX:(float)originX originY:(float)originY{    if ( [(NSButton *)displayEnable intValue] == 0 ) {		[(NSTextField *)displayOriginX setFloatValue:originX];		[(NSTextField *)displayOriginY setFloatValue:originY];	}}- (float) originX{	return [(NSTextField *)displayOriginX floatValue];}- (float) originY{    return [(NSTextField *)displayOriginY floatValue];}- (int) frameAspectEnabled{	return [(NSButton *)frameEnable intValue];}- (void) frameAspect:(float)fa{    if ( [(NSButton *)frameEnable intValue] == 0 ) {		[(NSTextField *)frameAspect setFloatValue:fa];	}}- (float) frameAspect{	return [(NSTextField *)frameAspect floatValue];}@end